class Attribute:
    number = 0

    def __init__(self, key: str, value):
        self.key = key
        self.value = value
        Attribute.number += 1

    def get_key(self) -> str:
        return self.key

    def get_value(self):
        return self.value

    def __repr__(self):
       return "Key: {} Value: {}".format(self.key, self.value)
