class Attribute:
    """
    Class represents attributes regarding to a process. Following process objects can have attributes:
        1. Process instances \n
        2. Activities
    """
    def __init__(self, key: str, value):
        self.__key = key
        self.__value = value

    def __eq__(self, other):
        if self.key == other.key and self.value == other.value:
            return True
        else:
            return False

    def __repr__(self):
        return "Key: {}, Value: {}".format(self.key, self.value)

    @property
    def key(self):
        return self.__key

    @key.setter
    def key(self, key):
        self.__key = key

    @property
    def value(self):
        return self.__value

    @value.setter
    def value(self, value):
        self.__value = value



