import pandas as pd
from openPM.Process.Activity import Activity
from openPM.Process.Attribute import Attribute


class Instance:
    number = 0

    def __init__(self):
        self.instance = pd.DataFrame()
        self.activities = []
        self.attributes = []  # TODO Attributes are stored but still not in the dataFrame!
        Instance.number += 1

    def add_activity(self, activity: Activity):
        self.activities.append(activity)

    def add_attribute(self, attribute: Attribute):
        self.activities.append(attribute)

    def get_attributes(self):
        return self.attributes

    def get_activities(self):
        return self.activities

    def get_instance(self):
        return self.activities
